import React, { useState } from 'react';

import Card from "../UI/Card";
import Button from "../UI/Button";
import classes from './AddUser.module.css'

const AddUser = props =>{
    const [enteredUserName, setEnteredUserName]= useState('');
    const [enteredUserAge, setEnteredAge]= useState('');


    const userNameHandler = (event) =>{
        setEnteredUserName(event.target.value);
    }
    const userAgeHandler = (event) =>{
        setEnteredAge(event.target.value);
    }

    const addUserHandler = (event) => {
        event.preventDefault()
        if (enteredUserName.trim().length === 0){
            return;
        } else if(enteredUserAge < 1){
            return;
        }
        console.log(enteredUserName,enteredUserAge)
        setEnteredUserName('');
        setEnteredAge('');
    }
    return(
        <Card className={classes.input}>
            <form onSubmit={addUserHandler}>
                <label id='label' htmlFor='username'>Username</label>
                <input id='username' type='text' value={enteredUserName} onChange={userNameHandler}/>
                <label htmlFor='user-age'>Age (Years)</label>
                <input id='user-age' type='number' value={enteredUserAge} onChange={userAgeHandler}/>
                <Button onClick={props.onClick} type='submit'>Add User</Button>
            </form>
        </Card>
    );
}

export default AddUser;
